mapper.select:
SELECT po.poid, po.postate, po.pocnt, po.pocode, po.clientid, po.itemid, cli.clientname, cli.clientcode, item.itemname, item.itemcode, po.ordersdate, po.updatedate, po.ordersduedate, po.membercode, re.receiptid, cli.businessnumber, cli.representative, cli.clientaddress, cli.clientphone, cli.clientfax, item.origin, item.itemweight, item.itemprice,re.sign, mem.cafeinNumber, mem.cafeinName,mem.cafeinRepresent, mem.cafeinAddr, mem.cafeinFax, mem.cafeinCall, mem.membername FROM purchaseorder po JOIN item ON po.itemid = item.itemid JOIN client cli ON po.clientid = cli.clientid left JOIN receipt re ON po.receiptid = re.receiptid left join member mem on po.membercode = mem.membercode
postate=#{postate}
cli.clientname LIKE CONCAT('%', #{searchText}, '%')
and DATE(po.ordersdate) BETWEEN #{startDate} AND #{endDate}
ORDER BY ordersdate DESC, poid DESC LIMIT #{cri.startPage}, #{cri.pageSize}

SELECT count(*) FROM purchaseorder po JOIN item ON po.itemid = item.itemid JOIN client cli ON po.clientid = cli.clientid
postate=#{postate}
cli.clientname LIKE CONCAT('%', #{searchText}, '%')
and DATE(po.ordersdate) BETWEEN #{startDate} AND #{endDate}

mapper.update:
update purchaseorder set clientid=#{clientid}, itemid=#{itemid}, postate=#{postate}, pocnt=#{pocnt},
ordersdate=#{ordersdate}, ordersduedate=#{ordersduedate}, updatedate=#{updatedate}, membercode=#{membercode}
where poid=#{poid}
