mapper.select:
select count(*) from purchaseorder



		and pocode like '%AL%';


		and pocode like '%SK%';


		and pocode like '%FJ%';


		and pocode like '%GH%';

mapper.update:
update purchaseorder set clientid=#{clientid}, itemid=#{itemid}, postate=#{postate}, pocnt=#{pocnt},
ordersdate=#{ordersdate}, ordersduedate=#{ordersduedate}, membercode=#{membercode}
where poid=#{poid}
