mapper.select:
SELECT sh.shipid, sh.shipdate1, sh.shipcode, sh.workcode, sh.itemname, sh.clientname, sh.pocnt, sh.shipsts, sh.shipdate2, sh.membercode, mb.membername FROM ship sh JOIN member mb ON sh.membercode = mb.membercode
shipsts=#{shipsts}
AND (sh.clientname LIKE CONCAT('%', #{searchText}, '%') OR sh.itemname LIKE CONCAT('%', #{searchText}, '%') OR sh.shipcode LIKE CONCAT('%', #{searchText}, '%') OR sh.workcode LIKE CONCAT('%', #{searchText}, '%'))
and DATE(sh.shipdate1) BETWEEN #{startDate} AND #{endDate}
ORDER BY shipdate1 DESC, shipid DESC LIMIT #{cri.startPage}, #{cri.pageSize}

SELECT count(*) FROM ship sh JOIN member mb ON sh.membercode = mb.membercode
shipsts=#{shipsts}
AND (sh.clientname LIKE CONCAT('%', #{searchText}, '%') OR sh.itemname LIKE CONCAT('%', #{searchText}, '%') OR sh.shipcode LIKE CONCAT('%', #{searchText}, '%') OR sh.workcode LIKE CONCAT('%', #{searchText}, '%'))
and DATE(sh.shipdate1) BETWEEN #{startDate} AND #{endDate}
ORDER BY shipdate1 DESC, shipid DESC

mapper.update:
update ship set workcode=#{workcode}, clientname=#{clientname}, itemname=#{itemname}, shipsts=#{shipsts}, membercode=#{membercode},
pocnt=#{pocnt}, shipdate1=#{shipdate1}, shipdate2=#{shipdate2}
where shipid=#{shipid}

mapper.select:
SELECT distinct wk.workid, wk.workdate1, wk.workcode, wk.clientname, wk.pocode, wk.itemname, wk.worksts, wk.pocnt, wk.workdate2, wk.membercode, mb.membername FROM work wk JOIN member mb ON wk.membercode = mb.membercode LEFT JOIN purchaseorder po ON wk.pocode = po.pocode
worksts=#{worksts}
AND (wk.clientname LIKE CONCAT('%', #{searchText}, '%') OR wk.itemname LIKE CONCAT('%', #{searchText}, '%') OR wk.workcode LIKE CONCAT('%', #{searchText}, '%') OR wk.pocode LIKE CONCAT('%', #{searchText}, '%'))
and DATE(sh.shipdate1) BETWEEN #{startDate} AND #{endDate}
ORDER BY workdate1 DESC, workid DESC LIMIT #{cri.startPage}, #{cri.pageSize}

SELECT COUNT(*) FROM (SELECT distinct wk.workid, wk.workdate1, wk.workcode, wk.clientname, wk.pocode, wk.itemname, wk.worksts, wk.pocnt, wk.workdate2, wk.membercode FROM work wk JOIN member mb ON wk.membercode = mb.membercode JOIN purchaseorder po ON wk.pocode = po.pocode
worksts=#{worksts}
AND (wk.clientname LIKE CONCAT('%', #{searchText}, '%') OR wk.itemname LIKE CONCAT('%', #{searchText}, '%') OR wk.workcode LIKE CONCAT('%', #{searchText}, '%') OR wk.pocode LIKE CONCAT('%', #{searchText}, '%'))
and DATE(sh.shipdate1) BETWEEN #{startDate} AND #{endDate}
ORDER BY workdate1 desc, workid desc ) as subquery;

mapper.update:
update work set pocode=#{pocode}, clientname=#{clientname}, itemname=#{itemname}, worksts=#{worksts}, membercode=#{membercode},
pocnt=#{pocnt}, workdate1=#{workdate1}
where workid=#{workid}

mapper.select:
SELECT wk.workid, wk.workdate2, wk.workcode, wk.clientname, wk.itemname, wk.pocnt, wk.membercode, mb.membername, wk.returncount, wk.returnreason FROM work wk JOIN member mb ON wk.membercode = mb.membercode
wk.worksts = '완료'
AND (wk.clientname LIKE CONCAT('%', #{searchText}, '%') OR wk.itemname LIKE CONCAT('%', #{searchText}, '%') OR wk.workcode LIKE CONCAT('%', #{searchText}, '%'))
and DATE(wk.workdate2) BETWEEN #{startDate} AND #{endDate}
ORDER BY workid DESC LIMIT #{cri.startPage}, #{cri.pageSize}

mapper.update:
update work set workcode=#{workcode}, clientname=#{clientname}, itemname=#{itemname}, membercode=#{membercode},
pocnt=#{pocnt}, workdate1=#{workdate2}, returncount=#{returncount}, returnreason=#{returnreason}
where workid=#{workid}
