mapper.select:
SELECT q.qualityid, auditcode, q.itemtype, q.produceid, process, returnid, q.itemid, itemcode, i.itemname, auditbycode, weight, productquantity, auditquantity, normalquantity, defectquantity, auditstatus, DATE(registerationdate) AS 'registerationdate', DATE(completedate) AS 'completedate', quantity, registerstock, registerdefect FROM quality q JOIN item i ON q.itemid = i.itemid LEFT OUTER JOIN produce p ON q.produceid = p.produceid LEFT OUTER JOIN roastedbean r ON q.produceid = r.produceid
(q.itemtype = '생산' OR q.itemtype = '반품')
AND process = #{searchBtn}
AND q.itemtype = #{searchBtn}
AND DATE(registerationdate) BETWEEN #{startDate} AND #{endDate}
AND process = #{searchBtn} AND DATE(registerationdate) BETWEEN #{startDate} AND #{endDate}
AND q.itemtype = #{searchBtn} AND DATE(registerationdate) BETWEEN #{startDate} AND #{endDate}
ORDER BY q.qualityid DESC LIMIT #{cri.startPage}, #{cri.pageSize}

SELECT COUNT(*)
FROM quality q
JOIN item i
ON q.itemid = i.itemid
LEFT OUTER JOIN produce p
ON q.produceid = p.produceid
LEFT OUTER JOIN roastedbean r
ON q.produceid = r.produceid

(q.itemtype = '생산' OR q.itemtype = '반품')

AND process = #{searchBtn}


AND q.itemtype = #{searchBtn}


AND DATE(registerationdate) BETWEEN #{startDate} AND #{endDate}


AND process = #{searchBtn} AND
DATE(registerationdate) BETWEEN #{startDate} AND #{endDate}


AND q.itemtype = #{searchBtn} AND
DATE(registerationdate) BETWEEN #{startDate} AND #{endDate}

mapper.update:
UPDATE quality
SET auditbycode = #{auditbycode}, auditquantity = #{auditquantity}, 
normalquantity = #{normalquantity}, defectquantity = #{defectquantity},
auditstatus = #{auditstatus}, auditcode = #{auditcode}
WHERE qualityid = #{qualityid}

UPDATE quality
SET auditbycode = #{auditbycode}, auditquantity = #{auditquantity}, 
normalquantity = #{normalquantity}, defectquantity = #{defectquantity},
auditstatus = #{auditstatus}, completedate = NOW(), auditcode = #{auditcode}
WHERE qualityid = #{qualityid}

UPDATE returns 
SET qualityid = #{qualityid}
WHERE returnid = #{returnid}

UPDATE quality
SET auditbycode = #{auditbycode}, auditquantity = #{auditquantity}, 
normalquantity = #{normalquantity}, defectquantity = #{defectquantity},
auditstatus = #{auditstatus}, auditcode = #{auditcode}
WHERE qualityid = #{qualityid}

UPDATE quality
SET auditbycode = #{auditbycode}, auditquantity = #{auditquantity}, 
normalquantity = #{normalquantity}, defectquantity = #{defectquantity},
auditstatus = #{auditstatus}, completedate = NOW(), auditcode = #{auditcode}
WHERE qualityid = #{qualityid}

mapper.select:
SELECT defectid, d.qualityid, q.itemtype, process, itemcode, i.itemname, defectquantity, defecttype, processmethod, d.registerationdate FROM defects d JOIN quality q ON d.qualityid = q.qualityid LEFT OUTER JOIN produce p ON q.produceid = p.produceid JOIN item i ON q.itemid = i.itemid
(q.itemtype = '생산' OR q.itemtype = '반품')
AND process = #{searchBtn}
AND q.itemtype = #{searchBtn}
AND (q.itemtype LIKE CONCAT('%', #{searchText}, '%') OR process LIKE CONCAT('%', #{searchText}, '%') OR d.qualityid = #{searchText} OR itemcode LIKE CONCAT('%', #{searchText}, '%') OR i.itemname LIKE CONCAT('%', #{searchText}, '%'))
ORDER BY defectid DESC LIMIT #{cri.startPage}, #{cri.pageSize}

SELECT COUNT(*) FROM defects d JOIN quality q ON d.qualityid = q.qualityid LEFT OUTER JOIN produce p ON q.produceid = p.produceid JOIN item i ON q.itemid = i.itemid
(q.itemtype = '생산' OR q.itemtype = '반품')
AND process = #{searchBtn}
AND q.itemtype = #{searchBtn}
AND (q.itemtype LIKE CONCAT('%', #{searchText}, '%') OR process LIKE CONCAT('%', #{searchText}, '%') OR d.qualityid = #{searchText} OR itemcode LIKE CONCAT('%', #{searchText}, '%') OR i.itemname LIKE CONCAT('%', #{searchText}, '%'))

mapper.update:
UPDATE quality 
SET registerdefect = 'Y'
WHERE qualityid = #{qualityid}
