mapper.select:
SELECT sh.shipid, sh.shipdate1, sh.shipcode, sh.workcode, sh.stockid, st.stockquantity, sh.itemname, sh.clientname, sh.pocnt, sh.shipsts, sh.shipdate2, sh.membercode FROM ship sh LEFT JOIN stock st ON sh.stockid = st.stockid
shipsts=#{shipsts}
AND (sh.clientname = #{searchBtn} OR sh.itemname = #{searchBtn} OR sh.shipcode = #{searchBtn} OR sh.pocode = #{searchBtn})
AND ( sh.clientname LIKE CONCAT('%', #{searchText}, '%') OR sh.itemname LIKE CONCAT('%', #{searchText}, '%') OR sh.shipcode LIKE CONCAT('%', #{searchText}, '%') OR sh.pocode LIKE CONCAT('%', #{searchText}, '%') )
AND ( sh.clientname LIKE CONCAT('%', #{searchText}, '%') OR sh.itemname LIKE CONCAT('%', #{searchText}, '%') OR sh.shipcode LIKE CONCAT('%', #{searchText}, '%') OR sh.pocode LIKE CONCAT('%', #{searchText}, '%') )
AND (sh.clientname = #{searchBtn} OR sh.itemname = #{searchBtn} OR sh.shipcode = #{searchBtn} OR sh.pocode = #{searchBtn})
AND DATE(shipdate1) BETWEEN #{startDate} AND #{endDate}
ORDER BY shipdate1 DESC, shipid DESC LIMIT #{cri.startPage}, #{cri.pageSize}

mapper.update:
update ship set workcode=#{workcode}, clientname=#{clientname}, itemname=#{itemname}, shipsts=#{shipsts}, membercode=#{membercode},
pocnt=#{pocnt}, shipdate1=#{shipdate1}, shipdate2=#{shipdate2}
where shipid=#{shipid}

mapper.select:
SELECT distinct wk.workid, wk.workdate1, wk.workcode, wk.clientname, wk.pocode, wk.itemname, wk.worksts, wk.pocnt, wk.workupdate, wk.workdate2, wk.membercode FROM work wk LEFT JOIN purchaseorder po ON wk.pocode = po.pocode
worksts=#{worksts}
AND (wk.clientname = #{searchBtn} OR wk.itemname = #{searchBtn} OR wk.workcode = #{searchBtn} OR wk.pocode = #{searchBtn})
AND ( wk.clientname LIKE CONCAT('%', #{searchText}, '%') OR wk.itemname LIKE CONCAT('%', #{searchText}, '%') OR wk.workcode LIKE CONCAT('%', #{searchText}, '%') OR wk.pocode LIKE CONCAT('%', #{searchText}, '%') )
AND ( wk.clientname LIKE CONCAT('%', #{searchText}, '%') OR wk.itemname LIKE CONCAT('%', #{searchText}, '%') OR wk.workcode LIKE CONCAT('%', #{searchText}, '%') OR wk.pocode LIKE CONCAT('%', #{searchText}, '%') )
AND (wk.clientname = #{searchBtn} OR wk.itemname = #{searchBtn} OR wk.workcode = #{searchBtn} OR wk.pocode = #{searchBtn})
AND DATE(workdate1) BETWEEN #{startDate} AND #{endDate}
ORDER BY workdate1 DESC, workid DESC LIMIT #{cri.startPage}, #{cri.pageSize}

SELECT COUNT(*) FROM (SELECT distinct wk.workid, wk.workdate1, wk.workcode, wk.clientname, wk.pocode, wk.itemname, wk.worksts, wk.pocnt, wk.workdate2, wk.membercode FROM work wk JOIN purchaseorder po ON wk.pocode = po.pocode
worksts=#{worksts}
AND (wk.clientname = #{searchBtn} OR wk.itemname = #{searchBtn} OR wk.workcode = #{searchBtn} OR wk.pocode = #{searchBtn})
AND ( wk.clientname LIKE CONCAT('%', #{searchText}, '%') OR wk.itemname LIKE CONCAT('%', #{searchText}, '%') OR wk.workcode LIKE CONCAT('%', #{searchText}, '%') OR wk.pocode LIKE CONCAT('%', #{searchText}, '%') )
AND ( wk.clientname LIKE CONCAT('%', #{searchText}, '%') OR wk.itemname LIKE CONCAT('%', #{searchText}, '%') OR wk.workcode LIKE CONCAT('%', #{searchText}, '%') OR wk.pocode LIKE CONCAT('%', #{searchText}, '%') )
AND (wk.clientname = #{searchBtn} OR wk.itemname = #{searchBtn} OR wk.workcode = #{searchBtn} OR wk.pocode = #{searchBtn})
AND DATE(workdate1) BETWEEN #{startDate} AND #{endDate}
ORDER BY workdate1 desc, workid desc) as subquery;

mapper.update:
update work set pocode=#{pocode}, clientname=#{clientname}, itemname=#{itemname}, worksts=#{worksts}, membercode=#{membercode},
pocnt=#{pocnt}, workdate1=#{workdate1}, workupdate=#{workupdate}
where workid=#{workid}

mapper.select:
SELECT wk.workid, wk.workdate2, wk.workcode, wk.clientname, wk.itemname, wk.pocnt, wk.membercode, wk.returnyn, wk.returncount, wk.returnreason FROM work wk
wk.worksts = '완료'
AND ( wk.clientname LIKE CONCAT('%', #{searchText}, '%') OR wk.itemname LIKE CONCAT('%', #{searchText}, '%') OR wk.workcode LIKE CONCAT('%', #{searchText}, '%') OR wk.returnreason LIKE CONCAT('%', #{searchText}, '%') )
AND ( wk.clientname LIKE CONCAT('%', #{searchText}, '%') OR wk.itemname LIKE CONCAT('%', #{searchText}, '%') OR wk.workcode LIKE CONCAT('%', #{searchText}, '%') OR wk.returnreason LIKE CONCAT('%', #{searchText}, '%') )
AND wk.clientname = #{searchBtn}
AND DATE(workdate1) BETWEEN #{startDate} AND #{endDate}
ORDER BY workid DESC LIMIT #{cri.startPage}, #{cri.pageSize}

SELECT count(*) FROM work wk
wk.worksts = '완료'
AND ( wk.clientname LIKE CONCAT('%', #{searchText}, '%') OR wk.itemname LIKE CONCAT('%', #{searchText}, '%') OR wk.workcode LIKE CONCAT('%', #{searchText}, '%') OR wk.returnreason LIKE CONCAT('%', #{searchText}, '%') )
AND ( wk.clientname LIKE CONCAT('%', #{searchText}, '%') OR wk.itemname LIKE CONCAT('%', #{searchText}, '%') OR wk.workcode LIKE CONCAT('%', #{searchText}, '%') OR wk.returnreason LIKE CONCAT('%', #{searchText}, '%') )
AND wk.clientname = #{searchBtn}
AND DATE(workdate1) BETWEEN #{startDate} AND #{endDate}

mapper.update:
update work set workcode=#{workcode}, clientname=#{clientname}, itemname=#{itemname}, membercode=#{membercode},
pocnt=#{pocnt}, workdate1=#{workdate2}, returnyn=#{returnyn}, returncount=#{returncount}, returnreason=#{returnreason}
where workid=#{workid}
