mapper.select:
		SELECT it.itemname, st.stockquantity
		FROM stock st
        JOIN item it ON st.itemid = it.itemid;

mapper.update:
update ship set shipcode=#{shipcode}, workcode=#{workcode}, clientname=#{clientname}, itemname=#{itemname}, shipsts=#{shipsts}, membercode=#{membercode},
pocnt=#{pocnt}, shipdate1=#{shipdate1}, shipdate2=#{shipdate2}, lotnumber=#{lotnumber}
where shipid=#{shipid}

update work set pocode=#{pocode}, clientname=#{clientname}, itemname=#{itemname}, worksts=#{worksts}, membercode=#{membercode},
pocnt=#{pocnt}, workdate1=#{workdate1}, workupdate=#{workupdate}
where workid=#{workid}
