mapper.select:

SELECT sh.shipid, sh.shipdate1, sh.shipcode, sh.workcode,
sh.stockid, st.stockquantity, sh.itemname, sh.clientname,
sh.pocnt, sh.shipsts, sh.shipdate2, sh.membercode
FROM ship sh
LEFT JOIN stock st ON sh.stockid = st.stockid


	shipsts=#{shipsts}


	AND sh.clientname = #{searchBtn}


	AND sh.clientname LIKE CONCAT('%', #{searchText}, '%')


	AND sh.clientname LIKE CONCAT('%', #{searchText}, '%')


AND sh.clientname = #{searchBtn}


AND DATE(shipdate1) BETWEEN #{startDate} AND #{endDate}


ORDER BY shipdate1 DESC, shipid DESC
LIMIT #{cri.startPage}, #{cri.pageSize}

   SELECT count(*)
	FROM ship sh
	LEFT JOIN stock st ON sh.stockid = st.stockid


			shipsts=#{shipsts}


			AND sh.clientname = #{searchBtn}


			AND sh.clientname LIKE CONCAT('%', #{searchText}, '%')


			AND sh.clientname LIKE CONCAT('%', #{searchText}, '%')


		AND sh.clientname = #{searchBtn}


		AND DATE(shipdate1) BETWEEN #{startDate} AND #{endDate}

mapper.update:
update ship set workcode=#{workcode}, clientname=#{clientname}, itemname=#{itemname}, shipsts=#{shipsts}, membercode=#{membercode},
pocnt=#{pocnt}, shipdate1=#{shipdate1}, shipdate2=#{shipdate2}
where shipid=#{shipid}

mapper.select:
SELECT wk.workid, wk.workdate1, wk.workcode, wk.clientname, wk.pocode, wk.itemname, wk.worksts, wk.pocnt, wk.workupdate, wk.workdate2, wk.membercode FROM work wk LEFT JOIN purchaseorder po ON wk.pocode = po.pocode
worksts=#{worksts}
AND wk.clientname = #{searchBtn}
AND wk.clientname LIKE CONCAT('%', #{searchText}, '%')
AND wk.clientname LIKE CONCAT('%', #{searchText}, '%')
AND wk.clientname = #{searchBtn}
AND DATE(workdate1) BETWEEN #{startDate} AND #{endDate}
GROUP BY wk.workid, wk.workdate1, wk.workcode, wk.pocode, wk.itemname, wk.worksts, wk.pocnt, wk.workupdate, wk.workdate2, wk.membercode ORDER BY workdate1 DESC, workid DESC LIMIT #{cri.startPage}, #{cri.pageSize}

   SELECT count(*)
	FROM work wk
	LEFT JOIN purchaseorder po ON wk.pocode = po.pocode


			worksts=#{worksts}


			AND wk.clientname = #{searchBtn}


			AND wk.clientname LIKE CONCAT('%', #{searchText}, '%')


			AND wk.clientname LIKE CONCAT('%', #{searchText}, '%')


		AND wk.clientname = #{searchBtn}


		AND DATE(workdate1) BETWEEN #{startDate} AND #{endDate}

mapper.update:
update work set pocode=#{pocode}, clientname=#{clientname}, itemname=#{itemname}, worksts=#{worksts}, membercode=#{membercode},
pocnt=#{pocnt}, workdate1=#{workdate1}, workupdate=#{workupdate}
where workid=#{workid}

mapper.select:
    SELECT wk.workid, wk.workdate2, wk.workcode, wk.clientname, wk.itemname,
           wk.pocnt, wk.membercode, wk.returncount, wk.returnreason
    FROM work wk


  	    wk.worksts = '완료'


			AND wk.clientname LIKE CONCAT('%', #{searchText}, '%')


			AND wk.clientname LIKE CONCAT('%', #{searchText}, '%')


			AND wk.clientname = #{searchBtn}


			AND DATE(workdate1) BETWEEN #{startDate} AND #{endDate}


    ORDER BY workid DESC 
    LIMIT #{cri.startPage}, #{cri.pageSize}

   SELECT count(*)
	FROM work wk

	wk.worksts = '완료'


			AND wk.clientname = #{searchBtn}


			AND wk.clientname LIKE CONCAT('%', #{searchText}, '%')


			AND wk.clientname LIKE CONCAT('%', #{searchText}, '%')


		AND wk.clientname = #{searchBtn}


		AND DATE(workdate1) BETWEEN #{startDate} AND #{endDate}

mapper.update:
update work set workcode=#{workcode}, clientname=#{clientname}, itemname=#{itemname}, membercode=#{membercode},
pocnt=#{pocnt}, workdate1=#{workdate2}, returncount=#{returncount}, returnreason=#{returnreason}
where workid=#{workid}
